    /**
     * 根据url获取html或者json数据
     * @param url
     * @return
     */
    public static String getContent(String url) {
        CloseableHttpClient client = HttpClients.createDefault();
        HttpGet httpGet = new HttpGet(url);
        String content = null;
        try {
            CloseableHttpResponse response = client.execute(httpGet);
            if (response.getStatusLine().getStatusCode() == 200) {
                HttpEntity entity = response.getEntity();
                content = EntityUtils.toString(entity, "utf8");
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                client.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        return content;
    }



    /**
     * 获取所有省份及其URl
     *
     * @return
     */
    public static Map getProvinceWithUrl() {
        HashMap<String, String> provinceUrlMap = new HashMap<>();
        Document document = null;
        try {
            document = Jsoup.connect("https://daxue.eol.cn/mingdan.shtml").get();
            Elements elements = document.select(".province>a");
            for (Element element : elements) {
                String url = element.attr("href");
                String province = element.text();
                provinceUrlMap.put(province, url);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        return provinceUrlMap;
    }

