    /**
     * 省份表
     */
    @Test
    public void provinceToSql() {
        Map<String, String> proMap = new HashMap<>();
        for (int i = 1; i <= 148; i++) {
            String fileName = "D:\\Downloads\\高校志愿推荐\\pages\\";
            fileName += "page_" + i + ".txt";
            File file = new File(fileName);
            try {
                BufferedReader br = new BufferedReader(new FileReader(file));
                StringBuffer sb = new StringBuffer();
                String s;
                while ((s = br.readLine()) != null) {
                    sb.append(s);
                }
                String json = sb.toString();
                List schoolList = JsonPath.read(json, "$.data.item[*]");
                for (Object school : schoolList) {
                    String name = JsonPath.read(school, "$.province_name").toString();
                    String id = JsonPath.read(school, "$.province_id").toString();
                    proMap.put(id, name);
                }
                br.close();

            } catch (FileNotFoundException e) {
                e.printStackTrace();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        proMap.forEach((key, value) -> {
            String sql = "insert into province(id,name) values(?,?)";
            jdbcTemplate.update(sql, key, value);
        });
        //+台湾
        String sql = "insert into province(id,name) values(?,?)";
        jdbcTemplate.update(sql, 71, "台湾");
    }

